import React, { useState, useEffect, useRef } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  useLocation,
} from "react-router-dom";
import { Helmet } from "react-helmet";
import styled, { ThemeProvider, keyframes } from "styled-components";
import {
  Pause,
  Play,
  SkipBack,
  SkipForward,
  Volume2,
  VolumeX,
} from "lucide-react";
import Navbar from "./Components/Navbar";
import Hero from "./Components/Sections/Hero";
import { darkTheme } from "./utils/Themes";
import Skills from "./Components/Sections/Skills";
import Footer from "./Components/Sections/Footer";
import Education from "./Components/Sections/Education";
import Experience from "./Components/Sections/Experience";
import StartCanvas from "./Components/canvas/Stars";
import Contact from "./Components/Sections/Contact";
const tracks = [
  {
    name: "Sky full of stars",
    artist: "Coldplay",
    path: "/music/skyfull.mp3",
    cover:
      "data:image/jpeg;base64,",
  },
  {
    name: "Timeless",
    artist: "The Weeknd, Playboi Carti",
    path: "/music/Timeless.mp3",
    cover:
      "data:image/jpeg;base64,",
  },
  {
    name: "Viva la Vida",
    artist: "Coldplay",
    path: "/music/VivaLaVida.mp3",
    cover:
      "data:image/jpeg;base64,",
  },
];

const Body = styled.div`
  background-color: ${({ theme }) => theme.bg};
  width: 100%;
  overflow-x: hidden;
  position: relative;
`;

const Wrapper = styled.div`
  padding-bottom: 100px;
  background: linear-gradient(
      38.73deg,
      rgba(204, 0, 187, 0.15) 0%,
      rgba(201, 32, 184, 0) 50%
    ),
    linear-gradient(
      141.27deg,
      rgba(0, 70, 209, 0) 50%,
      rgba(0, 70, 209, 0.15) 100%
    );
  width: 100%;
  clip-path: polygon(0 0, 100% 0, 100% 100%, 30% 98%, 0 100%);
`;

const rotate = keyframes`
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
`;

const MusicPlayer = styled.div`
  position: fixed;
  bottom: 2rem;
  right: 2rem;
  z-index: 1000;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  background: transparent;
  padding: 0;
  border-radius: 3rem;
  transition: all 0.5s cubic-bezier(0.25, 0.1, 0.25, 1);
  width: 3.5rem;
  height: 3.5rem;
  overflow: hidden;

  &:hover {
    width: auto;
    height: auto;
    padding: 0.5rem 1rem;
    gap: 1rem;
    background: rgba(0, 0, 0, 0.85);
    backdrop-filter: blur(10px);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
  }
`;

const AlbumArtWrapper = styled.div`
  position: relative;
  width: 3.5rem;
  height: 3.5rem;
  flex-shrink: 0;
  transition: transform 0.3s ease;

  &:hover {
    transform: scale(1.05);
  }

  &::after {
    content: "";
    position: absolute;
    inset: -2px;
    border-radius: 50%;
    background: ${({ theme }) => theme.primary};
    z-index: 0;
    opacity: 0.7;
    transition: opacity 0.3s ease;
  }

  &:hover::after {
    opacity: 0.9;
  }
`;

const AlbumArt = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  border-radius: 50%;
  overflow: hidden;
  cursor: pointer;
  z-index: 1;
  animation: ${(props) => (props.isPlaying ? rotate : "none")} 20s linear
    infinite;
  transition: all 0.3s ease;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.3s ease;
  }

  &:hover img {
    transform: scale(1.1);
  }
`;

const Controls = styled.div`
  display: flex;
  align-items: center;
  gap: 0.75rem;
  opacity: 0;
  visibility: hidden;
  transform: translateX(20px);
  transition: all 0.5s cubic-bezier(0.25, 0.1, 0.25, 1);
  white-space: nowrap;
  flex-shrink: 0;
  order: 1;

  ${MusicPlayer}:hover & {
    opacity: 1;
    visibility: visible;
    transform: translateX(0);
  }
`;

const ControlButton = styled.button`
  background: none;
  border: none;
  color: white;
  cursor: pointer;
  transition: all 0.3s cubic-bezier(0.25, 0.1, 0.25, 1);
  padding: 0.5rem;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;

  &:hover {
    color: ${({ theme }) => theme.primary};
    transform: scale(1.1);
    background: rgba(255, 255, 255, 0.1);
  }
`;

const VolumeControl = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-left: 0.5rem;
  padding-left: 0.5rem;
  border-left: 1px solid rgba(255, 255, 255, 0.2);
  opacity: 0;
  visibility: hidden;
  transform: translateX(-20px);
  transition: all 0.5s cubic-bezier(0.4, 0, 0.2, 1);
  flex-shrink: 0;

  ${MusicPlayer}:hover & {
    opacity: 1;
    visibility: visible;
    transform: translateX(0);
  }
`;

const VolumeSlider = styled.input`
  -webkit-appearance: none;
  width: 80px;
  height: 4px;
  border-radius: 2px;
  background: rgba(255, 255, 255, 0.2);
  outline: none;
  transition: all 0.3s ease;

  &::-webkit-slider-thumb {
    -webkit-appearance: none;
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background: ${({ theme }) => theme.primary};
    cursor: pointer;
    transition: all 0.3s ease;
  }

  &::-webkit-slider-thumb:hover {
    transform: scale(1.2);
  }

  &:hover {
    background: rgba(255, 255, 255, 0.3);
  }
`;

function App() {
  const location = useLocation();
  const isResumePage = location.pathname === "/resume";
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTrack, setCurrentTrack] = useState(0);
  const [volume, setVolume] = useState(0.7);
  const [isMuted, setIsMuted] = useState(false);
  const audioRef = useRef(null);
  const previousVolumeRef = useRef(volume);

  // Initial audio setup
  useEffect(() => {
    audioRef.current = new Audio();
    audioRef.current.volume = volume;

    return () => {
      if (audioRef.current) {
        audioRef.current.pause();
        audioRef.current = null;
      }
    };
  }, []);

  // Handle track changes and playback
  useEffect(() => {
    if (!audioRef.current || !tracks) return;

    const audio = audioRef.current;
    audio.src = tracks[currentTrack].path;
    audio.volume = isMuted ? 0 : volume;

    const handleEnded = () => {
      playNext();
    };

    audio.addEventListener("ended", handleEnded);

    if (isPlaying) {
      const playPromise = audio.play();
      if (playPromise !== undefined) {
        playPromise.catch((error) => {
          console.log("Playback failed:", error);
          setIsPlaying(false);
        });
      }
    } else {
      audio.pause();
    }

    return () => {
      audio.removeEventListener("ended", handleEnded);
    };
  }, [currentTrack, isPlaying, volume, isMuted]);

  const togglePlay = () => {
    if (!audioRef.current || !tracks) return;

    if (isPlaying) {
      audioRef.current.pause();
    } else {
      const playPromise = audioRef.current.play();
      if (playPromise !== undefined) {
        playPromise.catch((error) => {
          console.log("Playback failed:", error);
        });
      }
    }
    setIsPlaying(!isPlaying);
  };

  const playNext = () => {
    if (!tracks) return;
    const nextTrack = (currentTrack + 1) % tracks.length;
    setCurrentTrack(nextTrack);
  };

  const playPrevious = () => {
    if (!tracks) return;
    const prevTrack = (currentTrack - 1 + tracks.length) % tracks.length;
    setCurrentTrack(prevTrack);
  };

  const handleVolumeChange = (e) => {
    if (!audioRef.current) return;

    const newVolume = parseFloat(e.target.value);
    setVolume(newVolume);
    audioRef.current.volume = newVolume;
    previousVolumeRef.current = newVolume;

    if (newVolume === 0) {
      setIsMuted(true);
    } else if (isMuted) {
      setIsMuted(false);
    }
  };

  const toggleMute = () => {
    if (!audioRef.current) return;

    if (isMuted) {
      setVolume(previousVolumeRef.current);
      audioRef.current.volume = previousVolumeRef.current;
    } else {
      previousVolumeRef.current = volume;
      audioRef.current.volume = 0;
      setVolume(0);
    }
    setIsMuted(!isMuted);
  };

  return (
    <ThemeProvider theme={darkTheme}>
      {!isResumePage && <Navbar />}
      {!isResumePage && (
        <Body>
          <StartCanvas />
          <div>
            <Routes>
              <Route
                path="/"
                element={
                  <>
                    <Hero />
                    <Wrapper>
                      <Skills />
                      <Experience />
                    </Wrapper>
                    <Wrapper>
                      <Education />
                      <Contact />
                    </Wrapper>
                    <Footer />
                  </>
                }
              />
            </Routes>
          </div>

          <MusicPlayer>
            <AlbumArtWrapper>
              <AlbumArt isPlaying={isPlaying} onClick={togglePlay}>
                <img
                  src={
                    tracks?.[currentTrack]?.cover ||
                    "https://images.unsplash.com/photo-1470225620780-dba8ba36b745?w=300&h=300&fit=crop"
                  }
                  alt="Album Cover"
                />
              </AlbumArt>
            </AlbumArtWrapper>
            <Controls>
              <ControlButton onClick={playPrevious}>
                <SkipBack size={20} />
              </ControlButton>
              <ControlButton onClick={togglePlay}>
                {isPlaying ? <Pause size={24} /> : <Play size={24} />}
              </ControlButton>
              <ControlButton onClick={playNext}>
                <SkipForward size={20} />
              </ControlButton>
            </Controls>
            <VolumeControl>
              <ControlButton onClick={toggleMute}>
                {isMuted ? <VolumeX size={20} /> : <Volume2 size={20} />}
              </ControlButton>
              <VolumeSlider
                type="range"
                min="0"
                max="1"
                step="0.01"
                value={isMuted ? 0 : volume}
                onChange={handleVolumeChange}
              />
            </VolumeControl>
          </MusicPlayer>
        </Body>
      )}
    </ThemeProvider>
  );
}

function AppWrapper() {
  return (
    <Router>
      <Helmet>
        <meta charSet="utf-8" />
        <title>Harrison Shulman</title>
        <link rel="canonical" href="http://vihaanphal.vercel.app" />
      </Helmet>
      <App />
    </Router>
  );
}

export default AppWrapper;
